Ext.define("Command.module.Package",{extend:"Command.module.Abstract",description:"Packages a Sencha Touch 2 application for native app stores",actions:{generate:["Generates a Packager configuration JSON file",["path","p","What to call the configuration file","string","packager.json","myconfigfile.json"]],build:["Packages an app with the given configuration JSON file",["path","p","What to call the configuration file","string","packager.json","myconfigfile.json"]],run:["Packages and tries to run the application for the given configuration JSON file",["path","p","What to call the configuration file","string","packager.json","myconfigfile.json"]]},generate:function(a){this.exec("stbuild generate %s",[a],function(a,b,c){b&&this.info(b),c&&this.error(c)})},build:function(a){this.exec("stbuild package %s",[a],function(a,b,c){b&&this.info(b),c&&this.error(c)})},run:function(a,b){this.exec("stbuild run %s",[a],function(a,c,d){b&&b(),c&&this.info(c),d&&this.error(d)})}});